===OOP version===
<source lang="php">
 class fibonacci {

 	public $Begin	= 0;
 	public $Next;
 	public $Amount;
 	public $i;

 	public function __construct( $Begin, $Amount ) {

 		$this->Begin	= 0;
 		$this->Next		= 1;
 		$this->Amount	= $Amount;

 	}

 	public function _do() {

 		for( $this->i = 0; $this->i < $this->Amount; $this->i++ ) {

 			$Value = ( $this->Begin + $this->Next );

 			echo $this->Begin . ' + ' . $this->Next . ' = ' . $Value . '<br />';

 			$this->Begin	= $this->Next;
 			$this->Next		= $Value;

 		}

 	}

 }

 $Fib = new fibonacci( 0, 6 );
 echo $Fib->_do();
</source>
