	  // too bad JavaScript doesn't have a locale-based way of doing this
	  if (span) {
	    var date = new Date();
	    var todayYear = date.getYear();
	    var todayMonth = 1+date.getMonth();
	    var todayDay = date.getDate();
	    date.setISO8601(this.stringValue);
	    var month = 1+date.getMonth();
	    var day = date.getDate();
	    var year = date.getYear();
	    var hour = date.getHours();
	    var minute = date.getMinutes();
	    var second = date.getSeconds();
	    if (hour < 10) hour = "0" + hour;
	    if (minute < 10) minute = "0" + minute;
	    if (second < 10) second = "0" + second;
	    if (todayYear == year && todayMonth == month && todayDay == day) { 
	      span.textContent = hour + ":" + minute + ":" + second;
	    } else {
	      if (month < 10) month = "0" + month;
	      if (day < 10) day = "0" + day;
	      if (year > 100) year = year - 100;
	      if (year < 10) year = "0" + year;
	      // I18N Alert!
	      span.textContent = month + "/" + day + "/" + year + " " + hour + ":" + minute + ":" + second;
	    }
	  }
	  return true;]]>
	</body>
      </method>
    </implementation>
  </binding>

