// Indicate where you would like the link to appear:
// 1 is first (before username), 2 is second (before talk link), ... 7 is last (after log out link)
insertBeforeNumA = 4;

// Do NOT edit below this line unless you're experiened in javascript
insertBeforeArrA = new Array("","pt-userpage","pt-mytalk","pt-preferences","pt-watchlist","pt-mycontris","pt-logout","");
insertBeforeA = insertBeforeArrA[insertBeforeNumA];

function makeLink()
{
  var li = document.createElement( 'li' );
  li.id = 'pt-recentchangeslinked';

  var myAnchor = document.createElement( 'a' );
  myAnchor.setAttribute ('href', '/wiki/Special:Recentchangeslinked/User:Iamunknown/Watchlist'); 
  myAnchor.appendChild( document.createTextNode( 'images' ) );

  li.appendChild( myAnchor );

  if ( insertBeforeA )
  {
    var before = document.getElementById( insertBeforeA );
    before.appendChild( li, before );
  }
  else // append to end (right) of list
  {
    document.getElementById( 'pt-watchlist' ).parentNode.appendChild( li );
  }

}

if      ( window.addEventListener ) window.addEventListener ( 'load', makeLink, false );
else if ( window.attachEvent      ) window.attachEvent      ( 'onload', makeLink      );

