	str += 'if(admin){*&lt;&lt;unprotect|unprotectShort&gt;&gt;|&lt;&lt;protect|shortcut=p&gt;&gt;*' +
	'&lt;&lt;undelete|undeleteShort&gt;&gt;|&lt;&lt;delete|shortcut=d&gt;&gt;}';
