	var protect='&lt;menurow&gt;&lt;&lt;unprotect|unprotectShort&gt;&gt;|' +
	'&lt;&lt;protect|shortcut=p&gt;&gt;|&lt;&lt;protectlog|log&gt;&gt;&lt;/menurow&gt;';
