	'&lt;menurow&gt;&lt;&lt;edit|shortcut=e&gt;&gt;|&lt;&lt;editOld|shortcut=e|this&amp;nbsp;revision&gt;&gt;&lt;/menurow&gt;' +
	'&lt;menurow&gt;&lt;&lt;revert|shortcut=v&gt;&gt;|&lt;&lt;undo&gt;&gt;&lt;/menurow&gt;' + '}else{&lt;&lt;edit|shortcut=e&gt;&gt;}';
