	// popups menu starts here
	if (getValueOf('popupSetupMenu') &amp;&amp; !x.navpop.hasPopupMenu /* FIXME: hack */) {
		x.navpop.hasPopupMenu=true;
		s.push('*' + dropdiv + menuTitle('popupsMenu') + '&lt;menu&gt;');
