<table cellspacing=0 border=1 cellpadding=0>
  <tr style="background:#ffdead">  <td>COM_ID</td> <td>NAME</td> </tr>
  <tr><td>1</td> <td>ACME</td></tr>
  <tr><td>2</td> <td>Wikimedia</td></tr>
</table>

