var rt = {
  rt.span: "WKRTSpan",
  rt.iframe: null,
  rt.alias: new Array(),
};

rt.getIFrame = function() {
  if(rt.iframe == null) rt.iframe = wk.makeElement("iframe", {width:"100%", height:"550px"});
  return rt.iframe;
}

rt.talkLinks = function() {
   var frame = wk.makeElement("div");
   for(i = 0; i < rt.alias.length; i++) {
       var link = rt.createLink(rt.alias[i][0], rt.alias[i][1], rt.alias[i][2], rt.alias[i][3]);
       frame.appendChild(link);
       frame.appendChild(document.createTextNode(" — "));
   }
   link = wk.makeElement("a", {href:"#" + rt.span}, "Remove");
   link.onclick = rt.initFrame;
   frame.appendChild(link);
   return frame;
}

rt.createLink = function(project, username, linkname) {
  if(username == "") username = wgUserName;
  if(project  == "") project  = "wikibooks";
  if(linkname == "") linkname = username + "@" + project;
  username = username.replace(" ", "_");
  var link = wk.makeElement("a", {href:"#" + rt.span}, linkname);
  link.onclick = function(){ rt.getTalkPage(lang, project, username)};
  return link;
}

rt.getTalkPage = function (lang, project, username) {
  var talkurl = 
    "http://" + lang + "." + project + ".org" +
    "/w/index.php?title=User_talk:" + username +
    "&action=view&printable=yes";
  var frame = wk.spanText(rt.span, "");
  var iframe = rt.getIFrame();
  iframe.setAttribute("src", talkurl);
  frame.appendChild(rt.talkLinks());
  frame.appendChild(iframe);
  frame.appendChild(rt.talkLinks());
}

rt.getAlias = function(frame) {
  var text = frame.innerHTML;
  var links = text.split(",");
  for(var i = 0; i < links.length; i++) {
    var junk = links[i].split("@");
    var name = junk[0];
    var project = junk[1];
    rt.alias.push([project, name])
  }

}


rt.initFrame = function() {
  var frame = wk.spanText(rt.span, "");
  if(frame != null) {
    rt.getAlias(frame);
    frame.appendChild(rt.talkLinks());
  }
}

if(typeof wk == "object" && wk.testVersion(2.0)) {
  addOnloadHook(rt.initFrame);
}

window.twinkleConfigExists = false;

if ( userIsInGroup( 'sysop' ) || twUserIsWhitelisted() ) {
	twinkleConfigExists = true;
}

function twUserIsWhitelisted() {
	return userIsInGroup( 'autoconfirmed' ) || userIsInGroup( 'confirmed' );
}

if ( window.TwinkleConfig == undefined )
	window.TwinkleConfig = {};

if ( TwinkleConfig.portletId == undefined )
	TwinkleConfig.portletId = 'p-cactions';

/**
 * Add a portlet menu to one of the navigation areas on the page.
 * Available navigation areas depend on the script used.
 *
 * NOTE: If anyone is brave enough to reuse this directly, please shoot
 * me a note. Otherwise I might change the signature down the line and
 * your script breaks. Amalthea.
 *
 * @param String portlet -- id of the target portlet (skin dependent)
 * @param String id -- id of the portlet menu to create, preferably start with "p-".
 * @param String text -- name of the portlet menu to create. Visibility depends on the class used.
 * @param String type -- type of portlet. pass "menu" to make this portlet a drop down menu (vector only)
 * @param String next -- place before this menu, or otherwise at the end.
 *
 * @return jQuery -- the new portlet
 */
function twAddPortlet( portlet, id, text, type, next )
{
	var $portlet, $item, $where, $h5, outerDivClass, innerDivClass, skin;

	skin = mw.config.get( 'skin' );

	// verify/normalize input
	type = ( skin === "vector" && type === "menu" && ( portlet === "left-navigation" || portlet === "right-navigation" )) ? "menu" : "";

	switch ( skin )
	{
		case "vector":
			if ( $.inArray( portlet, [ "portal", "left-navigation", "right-navigation" ] ) === -1 ) {
				portlet = "mw-panel";
			}
			if ( portlet === "mw-panel" ) {
				outerDivClass = "portal";
				innerDivClass = "body";
			} else if ( type === "menu" ) {
				outerDivClass = "vectorMenu extraMenu emptyPortlet";
				innerDivClass = "menu";
			} else {
				outerDivClass = "vectorTabs extraMenu emptyPortlet";
				innerDivClass = "";
			}
			break;
		case "modern":
			if ( portlet !== "mw_contentwrapper" ) {
				portlet = "mw_portlets";
			}
			outerDivClass = "portlet";
			innerDivClass = "pBody";
			break;
		default:
			portlet = "column-one";
			outerDivClass = "portlet";
			innerDivClass = "pBody";
			break;
	}

	$portlet = $( '#' + portlet );
	$item = $( '#' + id );
	if ( $item.length ) {
		return $item.parent().is( $portlet ) ? $item : $();
	}

	// Build the DOM elements.
	$item = $( '<div>', { 'id': id, 'class': outerDivClass } );
	$h5 = $( '<h5>' );

	if ( type === "menu" ) {
		$h5
			.append( $( '<span>', { 'text': text } ) )
			.append( $( '<a>', { 'href': '#' } ).append( $( '<span>', { 'text': text } ) ) );
	} else {
		$h5.text( text );
	}

	$item.append( $h5 ).append( $( '<div>', { 'class': innerDivClass } ).append( '<ul></ul>' ) );

	if ( next ) {
		$where = $( '#' + next );
		if ( $where.parent().is( $portlet ) ) {
			$where.before( $item );
		} else {
			$portlet.append( $item );
		}
	} else {
		$portlet.append( $item );
	}

	return $item;
}

//Build a portlet menu if it doesn't exist yet, and add the portlet link.
function twAddPortletLink( task, text, id, tooltip, accesskey )
{
	var link;

	if (TwinkleConfig.portletArea) {
		twAddPortlet(
			TwinkleConfig.portletArea,
			TwinkleConfig.portletId,
			TwinkleConfig.portletName,
			TwinkleConfig.portletType,
			TwinkleConfig.portletNext
		);
	}
	link = mw.util.addPortletLink(
		TwinkleConfig.portletId,
		typeof task ==="string" ? task : "#",
		text, id, tooltip, accesskey, TwinkleConfig.portletNext
	);

	if ( $.isFunction(task) ) {
		$(link).click( function (e) { task.call(this, e); e.preventDefault(); } );
	}

	return link;
}

/**
 * QuickForm is for the creation of simple and standard forms without much specific coding.
 */
function QuickForm( event, eventType ) {
	this.root = new QuickForm.element({ type: 'form', event: event, eventType: eventType });
}

QuickForm.prototype.render = function QuickFormRender() {
	var ret = this.root.render();
	ret.names = {};
	return ret;
};

QuickForm.prototype.append = function QuickFormRender( data ) {
	return this.root.append( data );
};

QuickForm.element = function QuickFormElement( data ) {
	if ( !( this instanceof QuickForm.element ) ) {
		return new QuickForm.element( data );
	} else if ( data instanceof QuickForm.element ) {
		this.data = data.data;
		this.childs = data.childs;
		this.id = data.id;
	} else {
		this.data = data;
		this.childs = [];
		this.id = QuickForm.element.id++;
	}
};

QuickForm.element.id = 0;

QuickForm.element.prototype.append = function QuickFormElementAppend( data ) {
	var child = QuickForm.element( data );
	this.childs.push( child );
	return child;
};

QuickForm.element.prototype.render = function QuickFormElementRender() {
	var current = this.compute();
	$.each( this.childs, function() { current[1].append( this.render() ); } );
	return current[0];
};

QuickForm.element.prototype.compute = function QuickFormElementCompute( in_id ) {
	var	$node, $child, $label, data = this.data,
		id = ( in_id ? in_id + '_' : '' ) + 'node_' + this.id,
		cur_id, current, $cur_div, $input;

	if ( data.adminonly && !userIsInGroup( 'sysop' ) ) {
		// hell hack alpha
		data.type = 'hidden';
	}

	switch( data.type ) {
		case 'form':
			$node = $( '<form name="id" class="quickform" action="javascript:void(0);">' );
			if ( data.event ) {
				$.bind( data.eventType || 'submit', data.event );
			}
			break;
		case 'select':
			$node = $( '<div>', { 'id': 'div_' + id } );
			$child = $( '<select>', { 'name': data.name } );
			$node.append( $child );

			if ( data.label ) {
				$label = $( '<label>', { 'for': id, 'text': data.label } );
				$node.append( $label );
			}
			if ( data.multiple ) {
				$child.prop( 'multiple', true );
			}
			if ( data.size ) {
				$child.attr( 'size', data.size );
			}
			if ( data.event ) {
				$child.bind( 'change', data.event );
			}
			if ( data.list ) {
				for ( var i = 0; i < data.list.length; ++i ) {
					current = data.list[i];
					current.type = current.list ? 'optgroup' : 'option';
					$child.append( QuickForm.element( current ).compute()[0] );
				}
			}
			break;
		case 'option':
			$node = $( '<option>', {
				'value': data.value,
				'label': data.label,
				'text': data.label,
				'data': {
					'values': data.value
				}
			});
			if ( data.selected ) {
				$node.prop( 'selected', true );
			}
			if ( data.disabled ) {
				$node.prop( 'disabled', true );
			}
			break;
		case 'optgroup':
			$node = $( '<optgroup>', { 'label': data.label } );
			if ( data.list ) {
				for ( var i = 0; i < data.list.length; ++i ) {
					var current = data.list[i];
					current.type = 'option'; // must be options here
					$node.append( QuickForm.element( current ).compute()[0] );
				}
			}
			break;
		case 'field':
			$label = $( '<legend>', { 'text': data.label } );
			$node = $( '<fieldset>' ).append( $label );
			if ( data.name ) {
				$node.attr( 'name', data.name );
			}
			break;
		case 'checkbox':
		case 'radio':
			$node = $( '<div>' );
			if ( data.list ) {
				for ( var i = 0; i < data.list.length; ++i ) {
					cur_id = id + '_' + i;
					current = data.list[i];
					if ( current.type === 'header' ) {
						// inline hack
						$cur_div = $( '<h6>', { 'text': current.label } );
						$node.append( $cur_div );
						if ( current.tooltip ) {
							QuickForm.element.generateTooltip( $cur_div, current );
						}
						continue;
					}

					$cur_div = $( '<div>' );
					$input = $( '<input>', {
						'value':	current.value,
						'name':		current.name || data.name,
						'type':		data.type,
						'id':		cur_id,
						'data':		{
							'values':	current.value
						}
					});

					$cur_div.append( $input );
					$node.append( $cur_div );

					if ( current.checked ) {
						$input.prop( 'checked', true );
					}
					if ( current.disabled ) {
						$input.prop( 'disabled', true );
					}
					if ( data.event ) {
						$input.bind( 'change', data.event );
					} else if ( current.event ) {
						$input.bind( 'change', current.event );
					}

					$label = $( '<label>', { 'for': cur_id, 'text': current.label } );
					$cur_div.append( $label );

					if ( current.tooltip ) {
						QuickForm.element.generateTooltip( $label, current );
					}
					if ( current.subgroup ) {
						var tmpgroup, $subgroup;

						tmpgroup = $.extend( {}, current.subgroup );
						tmpgroup.name = (current.name || data.name) + '.' +  tmpgroup.name;

						if ( !tmpgroup.type ) {
							tmpgroup.type = data.type;
						}

						$subgroup = QuickForm.element( tmpgroup ).compute( cur_id )[0];

						$subgroup.style( 'marginLeft', '3em' );
						$input.data({ 'subgroup': $subgroup, 'shown': false });

						if ( current.checked ) {
							$cur_div.append( $subgroup );
						}

						$input.bind( 'change', function(e) {
							var $target = $( e.target ), name;

							if ( !e.target.checked ) {
								$target.data( 'subgroup' ).remove();
								return;
							}

							$target.parent().append( $target.data( 'subgroup' ) );

							if ( $target.attr( 'type' ) !== 'radio' ) {
								return;
							}

							name = $target.attr( 'name' );

							if ( typeof e.target.form.names[name] !== 'undefined' ) {
								e.target.form.names[name].data( 'subgroup' ).remove();
							}

							e.target.form.names[name] = $target;
						});
					} else if ( data.type === 'radio' ) {
						$input.bind( 'change', function(e) {
							var name = $( e.target ).attr( 'name' );
							if ( e.target.checked ) {
								if ( typeof e.target.form.names[name] !== 'undefined' ) {
									e.target.form.names[name].data( 'subgroup' ).remove();
								}
								delete e.target.form.names[name];
							} 
						});
					}
				}
			}
			break;
		case 'input':
			$node = $( '<div>' );

			if ( data.label ) {
				$node.append( $label = $( '<label>', { 'for': id, 'text': data.label } ) );
			}

			$input = $( '<input>', { 'name': data.name, 'type': 'text' } );
			$node.append( $input );

			if ( data.value ) {
				$input.val( data.value );
			}
			if ( data.size ) {
				$input.attr( 'size', data.size );
			}
			if ( data.disabled ) {
				$input.prop( 'disabled', true );
			}
			if ( data.readonly ) {
				$input.prop( 'readonly', true );
			}
			if ( data.maxlength ) {
				$input.attr( 'maxlength', data.maxlength );
			}
			if ( data.event ) {
				$input.bind( 'keyup', data.event );
			}
			break;
		case 'dyninput':
			var min = data.min || 1;
			var max = data.max || Infinity;

			$node = $( '<div>' ).append( $label = $( '<h5>', { 'text': data.label } ) );
			$cur_div = $( '<div>' );

			$node.append( $cur_div );

			current	= QuickForm.elements({
				type: 'button',
				label: 'more',
				disabled: min >= max,
				event: function(e) {
					var	data = $( e.target ).data(),
						area = data.area;
						elem = new QuickForm.element( data.sublist );

					e.stopPropagation();
					data.area.append( elem.render() );

					if ( ++data.counter >= data.max ) {
						$( e.target ).prop( 'disabled', true );
					}
				}
			}).compute();

			var sublist = {
				type:		'_dyninput_element',
				label:		data.sublabel || data.label,
				name:		data.name,
				value:		data.value,
				size:		data.size,
				remove:		false,
				maxlength:	data.maxlength,
				event:		data.event
			};

			$node.append( current[0] );

			for ( var i = 0; i < min; ++i ) {
				$cur_div.append( QuickForm.element( sublist ).render() );
			}

			$.extend( sublist, {
				'remove':	true,
				'button':	current[1],
				'elements':	$cur_div
			});
			current[1].data({
				'sublist':	sublist,
				'area':		$cur_node,
				'max':		max - min,
				'counter':	0
			});

			break;
		case '_dyninput_element': // Private, similar to normal input
			$node = $( '<div>' );

			if ( data.label ) {
				$label = $( '<label>', { 'for': id, 'text': data.label } )
				$node.append( $label );
			}

			$input = $( '<input>', { 'name': data.name, 'type': 'text' } );
			$node.append( $input );

			if ( data.value ) {
				$input.attr( 'value', data.value );
			}
			if ( data.size ) {
				$input.attr( 'size', data.size );
			}
			if ( data.maxlength ) {
				$input.attr( 'maxlength', data.maxlength );
			}
			if ( data.event ) {
				$input.bind( 'keyup', data.event );
			}
			if ( data.remove ) {
				current = QuickForm.element({
					type: 'button',
					label: 'remove',
					event: function(e) {
						var	data = $( e.target ).data(),
							$node = data.input,
							$more = data.button;

						$node.remove();
						--more.counter;
						$more.removeAttribute( 'disabled' );
						e.stopPropagation();
					}
				}).compute();

				$node.append( current[0] );
				current[1].data({ 'input': $node, 'button': data.button });
			}
			break;
		case 'hidden':
			$node = $( '<input>', {
				'name': data.name,
				'type': 'hidden',
				'value': data.value,
				'data': {
					'values':	data.value
				}
			});
			break;
		case 'header':
			$node = $( '<h5>', { 'text': data.label } );
			break;
		case 'div':
			$node = $( '<div>' );
			break;
		case 'submit':
			$node = $( '<span>' );
			$child = $( '<input>', { 'type': 'submit', 'name': data.name || 'submit' } );
			$node.append( $child );
			if ( data.label ) {
				$child.attr( 'value', data.label );
			}
			if ( data.disabled ) {
				$child.prop( 'disabled', true );
			}
			break;
		case 'button':
			$node = $( '<span>' );
			$child = $( '<input>', { 'type': 'button', 'name': data.name } );
			$node.append( $child );
			if ( data.label ) {
				$child.attr( 'value', data.label );
			}
			if ( data.disabled ) {
				$child.prop( 'disabled', true );
			}
			if ( data.event ) {
				$child.bind( 'click', data.event );
			}
			break;
		case 'textarea':
			$node = $( '<div>' );
			if ( data.label ) {
				$label = $( '<h5>', { 'for': id, 'text': data.label } );
				$node.append( $label );
			}
			$cur_div = $( '<textarea>', { 'name': data.name } );
			$node.append( $cur_div );
			if ( data.cols ) {
				$cur_div.attr( 'cols', data.cols );
			}
			if ( data.rows ) {
				$cur_div.attr( 'rows', data.rows );
			}
			if ( data.disabled ) {
				$cur_div.prop( 'disabled', true );
			}
			if ( data.readonly ) {
				$cur_div.prop( 'readonly', true );
			}
			if ( data.value ) {
				$cur_div.val( data.value );
			}
			break;
		default:
			throw new Error("QuickForm: Unknown element type " + data.type.toString());
	}

	if ( !$child ) {
		$child = $node;
	}
	if ( data.tooltip ) {
		QuickForm.element.generateTooltip( $label || $node , data );
	}
	if ( data.extra ) {
		$child.data( 'extra', extra );
	}

	$child.attr( 'id', data.id || id );
	return [ $node, $child ];
};

QuickForm.element.generateTooltip = function QuickFormElementGenerateTooltip( $node, data ) {
	$( '<span />', {
		'class':	'ui-icon ui-icon-help ui-icon-inline twinkle-tooltip'
	}).appendTo( $node ).tipsy({
		'fallback':	data.tooltip,
		'fade':		true,
		'gravity':	$.fn.tipsy.autoWE,
		'html':		true,
		'delayOut':	250
	});
};

/*
 * returns an array containing the values of elements with the given name, that has it's
 * checked property set to true. (i.e. a checkbox or a radiobutton is checked), or select options
 * that have selected set to true. (don't try to mix selects with radio/checkboxes, please)
 * Type is optional and can specify if either radio or checkbox (for the event
 * that both checkboxes and radiobuttons have the same name.
 */
HTMLFormElement.prototype.getChecked = function( name, type ) {
	var elements = this.elements[name];
	if( !elements ) { 
		// if the element doesn't exists, return null.
		return null;
	}
	var return_array = [];
	if( elements instanceof HTMLSelectElement ) {
		var options = elements.options;
		for( var i = 0; i < options.length; ++i ) {
			if( options[i].selected ) {
				if( options[i].values ) {
					return_array.push( options[i].values );
				} else {
					return_array.push( options[i].value );
				}

			}
		}
	} else if( elements instanceof HTMLInputElement ) {
		if( type != null && elements.type != type ) {
			return [];
		} else if( elements.checked ) {
			return [ elements.value ];
		}
	} else {
		for( var i = 0; i < elements.length; ++i ) {
			if( elements[i].checked ) {
				if( type != null && elements[i].type != type ) {
					continue;
				}
				if( elements[i].values ) {
					return_array.push( elements[i].values );
				} else {
					return_array.push( elements[i].value );
				}
			}
		}
	}
	return return_array;
}

/*
 * returns an array containing the values of elements with the given name, that has non-empty strings
 * type is "text" or given.
 */
HTMLFormElement.prototype.getTexts = function( name, type ) {
	type == type || 'text';
	var elements = this.elements[name];
	if( !elements ) { 
		// if the element doesn't exists, return null.
		return null;
	}
	var return_array = [];
	for( var i = 0; i < elements.length; ++i ) {
		if( elements[i].value != '' ) {
			return_array.push( elements[i].value );
		}
	}
	return return_array;
}
/**
* Will escape a string to be used in a RegExp
*/
RegExp.escape = function( text, space_fix ) {

	if ( !arguments.callee.sRE ) {
		arguments.callee.sRE = /(\/|\.|\*|\+|\?|\||\(|\)|\[|\]|\{|\}|\\|\$|\^)/g;
	}

	text = text.replace( arguments.callee.sRE , '\\$1' );

	// Special Mediawiki escape, underscore/space is the same, often at lest:

	if( space_fix ) {
		text = text.replace( / |_/g, '[_ ]' );
	}

	return text;

}

// Sprintf implementation based on perl similar
function sprintf() {
	if( arguments.length == 0 ) {
		throw "Not enough arguments for sprintf";
	}
	var result = "";
	var format = arguments[0];

	var index = 1;
	var current_index = 1;
	var flags = {};
	var in_operator = false;
	var relative = false;
	var precision = false;
	var fixed = false;
	var vector = false;
	var vector_delimiter = '.';


	for( var i = 0; i < format.length; ++i ) {
		var current_char = format.charAt(i);
		if( in_operator ) {
			switch( current_char ) {
			case 'i':
				current_char = 'd';
				break;
			case 'F':
				current_char = 'f';
				break;
			case '%':
			case 'c':
			case 's':
			case 'd':
			case 'u':
			case 'o':
			case 'x':
			case 'e':
			case 'f':
			case 'g':
			case 'X':
			case 'E':
			case 'G':
			case 'b':
				var value = arguments[current_index];
				if( vector ) {
					r = value.toString().split( '' );
					result += value.toString().split('').map( function( value ) {
							return sprintf.format( current_char, value.charCodeAt(), flags );
						}).join( vector_delimiter );
				} else {
					result += sprintf.format( current_char, value, flags );
				}
				if( !fixed ) {
					++index;
				}
				current_index = index;
				flags = {};
				relative = false;
				in_operator = false;
				precision = false;
				fixed = false;
				vector = false;
				vector_delimiter = '.';
				break;
			case 'v':
				vector = true;
				break;
			case ' ':
			case '0':
			case '-':
			case '+':
			case '#':
				flags[current_char] = true;
				break;
			case '*':
				relative = true;
				break;
			case '.':
				precision = true;
				break;
			}
			if( /\d/.test( current_char ) ) {
				var num = parseInt( format.substr( i ) );
				var len = num.toString().length;
				i += len - 1;
				var next = format.charAt( i  + 1 );
				if( next == '$' ) {
					if( num <= 0 || num >= arguments.length ) {
						throw "out of bound";
					}
					if( relative ) {
						if( precision ) {
							flags['precision'] = arguments[num];
							precision = false;
						} else if( format.charAt( i + 2 ) == 'v' ) {
							vector_delimiter = arguments[num];
						}else {
							flags['width'] = arguments[num];
						}
						relative = false;
					} else {
						fixed = true;
						current_index = num;
					}
					++i;
				} else if( precision ) {
					flags['precision'] = num;
					precision = false;
				} else {
					flags['width'] = num;
				}
			} else if ( relative && !/\d/.test( format.charAt( i + 1 ) ) ) {
				if( precision ) {
					flags['precision'] = arguments[current_index];
					precision = false;
				} else if( format.charAt( i + 1 ) == 'v' ) {
					vector_delimiter = arguments[current_index];
				} else {
					flags['width'] = arguments[current_index];
				}
				++index;
				if( !fixed ) {
					current_index++;
				}
				relative = false;
			}
		} else {
			if( current_char == '%' ) {
				in_operator = true;
				continue;
			} else {
				result += current_char;
				continue;
			}
		}
	}
	return result;
}

sprintf.format = function sprintfFormat( type, value, flags ) {

	// Similar to how perl printf works
	if( value == undefined ) {
		if( type == 's' ) {
			return '';
		} else {
			return '0';
		}
	}

	var result;
	var prefix = '';
	var fill = '';
	var fillchar = ' ';
	switch( type ) {
	case '%':
		result = '%';
		break;
	case 'c':
		result = String.fromCharCode( parseInt( value ) );
		break;
	case 's':
		result = value.toString();
		break;
	case 'd':
		result = parseInt( value ).toString();
		break;
	case 'u':
		result = Math.abs( parseInt( value ) ).toString(); // it's not correct, but JS lacks unsigned ints
		break;
	case 'o':
		result = (new Number( Math.abs( parseInt( value ) ) ) ).toString(8);
		break;
	case 'x':
		result = (new Number( Math.abs( parseInt( value ) ) ) ).toString(16);
		break;
	case 'b':
		result = (new Number( Math.abs( parseInt( value ) ) ) ).toString(2);
		break;
	case 'e':
		var digits = flags['precision'] ? flags['precision'] : 6;
		result = (new Number( value ) ).toExponential( digits ).toString();
		break;
	case 'f':
		var digits = flags['precision'] ? flags['precision'] : 6;
		result = (new Number( value ) ).toFixed( digits ).toString();
	case 'g':
		var digits = flags['precision'] ? flags['precision'] : 6;
		result = (new Number( value ) ).toPrecision( digits ).toString();
		break;
	case 'X':
		result = (new Number( Math.abs( parseInt( value ) ) ) ).toString(16).toUpperCase();
		break;
	case 'E':
		var digits = flags['precision'] ? flags['precision'] : 6;
		result = (new Number( value ) ).toExponential( digits ).toString().toUpperCase();
		break;
	case 'G':
		var digits = flags['precision'] ? flags['precision'] : 6;
		result = (new Number( value ) ).toPrecision( digits ).toString().toUpperCase();
		break;
	}

	if(flags['+'] && parseFloat( value ) > 0 && ['d','e','f','g','E','G'].indexOf(type) != -1 ) {
		prefix = '+';
	}

	if(flags[' '] && parseFloat( value ) > 0 && ['d','e','f','g','E','G'].indexOf(type) != -1 ) {
		prefix = ' ';
	}

	if( flags['#'] && parseInt( value ) != 0 ) {
		switch(type) {
		case 'o':
			prefix = '0';
			break;
		case 'x':
		case 'X':
			prefix = '0x';
			break;
		case 'b':
			prefix = '0b';
			break;
		}
	}

	if( flags['0'] && !flags['-'] ) {
		fillchar = '0';
	}

	if( flags['width'] && flags['width'] > ( result.length + prefix.length ) ) {
		var tofill = flags['width'] - result.length - prefix.length;
		for( var i = 0; i < tofill; ++i ) {
			fill += fillchar;
		}
	}

	if( flags['-'] && !flags['0'] ) {
		result += fill;
	} else {
		result = fill + result;
	}

	return prefix + result;
}

var Bytes = function ( value ) {
	if( typeof(value) == 'string' ) {
		var res = /(\d+) ?(\w?)(i?)B?/.exec( value );
		var number = res[1];
		var mag = res[2];
		var si = res[3];

		if( ! number ) {
			this.number = 0;
			return;
		}

		if( !si ) {
			this.value = number * Math.pow( 10, Bytes.magnitudes[mag] * 3 );
		} else {
			this.value = number * Math.pow( 2, Bytes.magnitudes[mag] * 10 );
		}
	} else {
		this.value = value;
	}
}

Bytes.magnitudes = {
	'': 0,
	'K': 1,
	'M': 2,
	'G': 3,
	'T': 4,
	'P': 5,
	'E': 6,
	'Z': 7,
	'Y': 8
}
Bytes.rmagnitudes = {
	0: '',
	1: 'K',
	2: 'M',
	3: 'G',
	4: 'T',
	5: 'P',
	6: 'E',
	7: 'Z',
	8: 'Y'
}

Bytes.prototype.valueOf = function() {
	return this.value;
}

Bytes.prototype.toString = function( magnitude ) {
	var tmp = this.value;
	if( magnitude ) {
		var si = /i/.test(magnitude);
		var mag = magnitude.replace( /.*?(\w)i?B?.*/g, '$1' );
		if( si ) {
			tmp /= Math.pow( 2, Bytes.magnitudes[mag] * 10 );
		} else {
			tmp /= Math.pow( 10, Bytes.magnitudes[mag] * 3 );
		}
		if( parseInt( tmp ) != tmp ) {
			tmp = (new Number( tmp ) ).toPrecision( 4 );
		}
		return tmp + ' ' + mag + (si?'i':'') +  'B';
	} else {
		// si per default
		var current = 0;
		while( tmp >= 1024 ) {
			tmp /= 1024;
			++current;
		}
		tmp = this.value / Math.pow( 2, current * 10 );
		if( parseInt( tmp ) != tmp ) {
			tmp = (new Number( tmp ) ).toPrecision( 4 );
		}
		return tmp + ' ' + Bytes.rmagnitudes[current] + ( current > 0 ? 'iB' : 'B' );
	}

}
String.prototype.ltrim = function stringPrototypeLtrim( chars ) {
	chars = chars || "\\s*";
	return this.replace( new RegExp("^[" + chars + "]+", "g"), "" );
}

String.prototype.rtrim = function stringPrototypeRtrim( chars ) {
	chars = chars || "\\s*";
	return this.replace( new RegExp("[" + chars + "]+$", "g"), "" );
}
String.prototype.trim = function stringPrototypeTrim( chars ) {
	return this.rtrim(chars).ltrim(chars);
}

String.prototype.splitWeightedByKeys = function stringPrototypeSplitWeightedByKeys( start, end, skip ) {
	if( start.length != end.length ) {
		throw 'start marker and end marker must be of the same length';
	}
	var level = 0;
	var initial = null;
	var result = [];
	if( !( skip instanceof Array ) ) {
		if( typeof( skip ) == 'undefined' ) {
			skip = [];
		} else if( typeof( skip ) == 'string' ) {
			skip = [ skip ];
		} else {
			throw "non-applicable skip parameter";
		}
	}
	for( var i  = 0; i < this.length; ++i ) {
		for( var j = 0; j < skip.length; ++j ) {
			if( this.substr( i, skip[j].length ) == skip[j] ) {
				i += skip[j].length - 1;
				continue;
			}
		}
		if( this.substr( i, start.length ) == start ) {
			if( initial == null ) {
				initial = i;
			}
			++level;
			i += start.length - 1;
		} else if( this.substr( i, end.length ) == end ) {
			--level;
			i += end.length - 1;
		}
		if( level == 0 && initial != null ) {
			result.push( this.substring( initial, i + 1 ) );
			initial = null;
		}
	}

	return result;
}

Array.prototype.uniq = function arrayPrototypeUniq() {
	var result = [];
	for( var i = 0; i < this.length; ++i ) {
		var current = this[i];
		if( result.indexOf( current ) == -1 ) {
			result.push( current );
		}
	}
	return result;
}

Array.prototype.dups = function arrayPrototypeUniq() {
	var uniques = [];
	var result = [];
	for( var i = 0; i < this.length; ++i ) {
		var current = this[i];
		if( uniques.indexOf( current ) == -1 ) {
			uniques.push( current );
		} else {
			result.push( current );
		}
	}
	return result;
}

Array.prototype.chunk = function arrayChunk( size ) {
	if( typeof( size ) != 'number' || size <= 0 ) { // pretty impossible to do anything :)
		return [ this ]; // we return an array consisting of this array.
	}
	var result = [];
	var current;
	for(var i = 0; i < this.length; ++i ) {
		if( i % size == 0 ) { // when 'i' is 0, this is always true, so we start by creating one.
			current = [];
			result.push( current );
		}
		current.push( this[i] );
	}
    return result;
}

var Unbinder = function unbinder( string ) {
	if( typeof( string ) != 'string' ) {
		throw "not a string";
	}
	this.content = string;
	this.counter = 0;
	this.history = {};
	this.prefix = '%UNIQ::' + Math.random() + '::';
	this.postfix = '::UNIQ%';
}

Unbinder.prototype = {
  unbind: function UnbinderUnbind( prefix, postfix ) {
    var re = new RegExp( prefix + '(.*?)' + postfix, 'g' );
    this.content = this.content.replace( re, Unbinder.getCallback( this ) );
  },
  rebind: function UnbinderRebind() {
    var content = this.content;
    content.self = this;
    for( var current in this.history )
      if( this.history.hasOwnProperty( current ) )
        content = content.replace( current, this.history[current] );
    return content;
  },
  prefix: null, // %UNIQ::0.5955981644938324::
  postfix: null, // ::UNIQ%
  content: null, // string
  counter: null, // 0++
  history: null // {}
};

Unbinder.getCallback = function UnbinderGetCallback(self) {
  return function UnbinderCallback( match , a , b ) {
    var current = self.prefix + self.counter + self.postfix;
    self.history[current] = match;
    ++self.counter;
    return current;
  };
};

function clone( obj, deep ) {
  var objectClone = new obj.constructor();
  for ( var property in obj )
    if ( !deep ) {
		objectClone[property] = obj[property];
	}
    else if ( typeof obj[property] == 'object' ) {
		objectClone[property] = clone( obj[property], deep );
	}
    else {
		objectClone[property] = obj[property];
	}
  return objectClone;
}

function ln( ns, title )	{
	var ns2ln = {
		'0'	:	'la',
		'1'	:	'lat',
		'2'	:	'lu',
		'3'	:	'lut',
		'4'	:	'lw',
		'5'	:	'lwt',
		'6'	:	'li',
		'7'	:	'lit',
		'8'	:	'lm',
		'9'	:	'lmt',
		'10':	'lt',
		'11':	'ltt',
		'12':	'lh',
		'13':	'lht',
		'14':	'lc',
		'15':	'lct',
		'100':	'lp',
		'101':	'lpt'
	};
	return "\{\{" + ns2ln[ns] + "|" + title + "\}\}";
}

// Helper functions to change case of a string
String.prototype.toUpperCaseFirstChar = function() {
	return this.substr( 0, 1 ).toUpperCase() + this.substr( 1 );
}

String.prototype.toLowerCaseFirstChar = function() {
	return this.substr( 0, 1 ).toLowerCase() + this.substr( 1 );
}

String.prototype.toUpperCaseEachWord = function( delim ) {
	delim = delim ? delim : ' ';
	return this.split( delim ).map( function(v) { return v.toUpperCaseFirstChar() } ).join( delim );
}

String.prototype.toLowerCaseEachWord = function( delim ) {
	delim = delim ? delim : ' ';
	return this.split( delim ).map( function(v) { return v.toLowerCaseFirstChar() } ).join( delim );
}

