// <source lang="javascript">
/*
	Whiteknights Core JavaScript
	Library to automate and simplify some common tasks, and provide an interface for use by
	other scripts. Including a number of AJAX-based functions for querying the server and
	posting edits. See [[User:Whiteknight/Gadgetscore]] for documentation.

	Developed by [[User:Whiteknight]]. Currently unmaintained.
*/

var wk = {
	_version: 2.81,

	testVersion: function(ver) { return ver && wk._version >= ver || true; },

	_getNode: function(elem) { return elem && typeof elem == "string" && document.getElementById(elem) || elem; },

	getDate: function() {
		var date = new Date();
		return (date.getMonth() + 1) + "/" + date.getDate() + "/" + date.getFullYear();
	}, 

	getQueryHash: function() {
		var qs = new Object(), query = location.search.substring(1);
		if (query == "") return qs;
		var pairs = query.split("&"), x = pairs.length;
		while (x--) {
			var attrval = pairs[x].split("=");
			qs[attrval[0]] = attrval[1];
		}
		return qs;
	},

	_httpmethods: [
		function() { return new XMLHttpRequest(); },
		function() { return new ActiveXObject("msxml2.XMLHTTP"); },
		function() { return new ActiveXObject("Microsoft.XMLHTTP"); }
	],

	_httpmethod: null,

	_initclient: function() {
		for (var i = 0; i < wk._httpmethods.length; i++) {
			try {
				var method = wk._httpmethods[i];
				var client = method();
				if (client != null) {
					wk._httpmethod = method;
				}
				return client;
			} catch(e) { continue; }
		}
		return null;
	},

	httpClient: function() { return (wk._httpmethod || wk._initclient()) && wk._httpmethod(); },

	_encodeData: function(dat) {
		var p = new Array();
		for (var name in dat) {
			var value = dat[name].toString();
			var pair = encodeURIComponent(name).replace(/%20/g, "+") + "=" +
				   encodeURIComponent(value).replace(/%20/g, "+");
			p.push(pair);
		}
		return p.join("&");
	},

	loadPage: function(url, callback, type) {
		if (callback == null || url == null) {
			return false;
		}
		var client = wk.httpClient();
		if (client == null) {
			return false;
		}
		client.onreadystatechange = function() {
			if (client.readyState != 4) return;
			if (type == "text/xml" && client.overrideMimeType) {
				callback(client.responseXML);
			} else if (client.status == 200) {
				callback(client.responseText);
			} else {
				callback('');
			}
		}
		client.open("GET", url, true);
		if (type == "text/xml" && client.overrideMimeType) {
			client.overrideMimeType(type);
		}
		client.send(null);
		return true;
	},

	loadWikiPage: function(page, callback) { 
		page = page.replace(/ /g, "_");
		return wk.loadPage(wk._URLBase + page, callback, "text/xml");
	},

	loadEditPage: function(page, callback) {
		return wk.loadPage(wk._editURL(page), callback, "text/xml");
	},

	loadWikiText: function(page, callback) {
		return wk.loadPage(wk._rawURL(page), callback, "text/x-wiki");
	},

	loadWikiTextSection: function(page, section, callback) {
		return wk.loadPage(wk._rawURL(page) + "&section=" + section, callback, "text/x-wiki");
	},

	_URLBase: "//en.wikibooks.org/w/index.php?title=",

	_rawURL: function(page) {
		return wk._URLBase + page.replace(/ /g, "_") + "&action=raw&ctype=text/x-wiki";
	},

	_editURL: function(page, section) {
		if (section == null) {
			return wk._URLBase + page.replace(/ /g, "_") + "&action=edit&printable=yes";
		} else {
			return wk._URLBase + page.replace(/ /g, "_") + "&action=edit&printable=yes" + "&section=" + section;
		}
	},

	_ensureXML: function(text) {
		if (text.getElementsByTagName) {
			return text;
		}
		var xmldom = new ActiveXObject("Microsoft.XMLDOM");
		xmldom.async = false;
		xmldom.loadXML(text);
		return xmldom.documentElement;
	},

	/* "opts" contains options pertinent to the edit:
		opts.section
		opts.minor
		opts.watch
		opts.filter
		opts.callback
	*/

	postEdit: function(page, text, summary, opts) {
		if (opts == null) opts = new Object();
		var client = wk.httpClient();
		client.onreadystatechange = function() {
			if (client.readyState != 4 || client.status != 200) {
				return;
			}
			if (client.overrideMimeType) {
				wk._postEdit2(client, client.responseXML, page, text, summary, opts);
			} else {
				wk._postEdit2(client, wk._ensureXML(client.responseText), page, text, summary, opts);
			}
		}
		var url = wk._editURL(page, (typeof opts.section != "undefined") ? (opts.section) : (null));
		client.open("GET", url, true);
		if (client.overrideMimeType) {
			client.overrideMimeType("text/xml");
		}
		client.send(null);
	},

	_postEdit2: function(client, xml, page, text, summary, opts) {
		var input = xml.getElementsByTagName("input");
		var data = new Array();
		var pagetext = xml.getElementsByTagName("textarea")[0].value;
		var watch = null;

		data["wpTextbox1"]  = (typeof text == "function") ? (text(pagetext)) : (text);
		data["wpRecreate"]  = true;
		data["wpSummary"]   = summary;

		if (typeof opts.minor != "undefined") data["wpMinoredit"] = "1";
		data["wpSection"]   = (typeof opts.section != "undefined") ? (opts.section) : ("");
		data["wpSave"]      = "Save page";

		for (var i = 0; i < input.length; i++) {
			var attribs = input[i].attributes;
			var name = ""; var value = "";
			for (var j = 0; j < attribs.length; j++) {
				if (attribs[j].name == "name")  { name = attribs[j].value; }
				else if (attribs[j].name == "value") { value = attribs[j].value; }
			}
			if (name == "wpStarttime") { data["wpStarttime"] = value; }
			else if (name == "wpEdittime")  { data["wpEdittime"]  = value; }
			else if (name == "wpEditToken") { data["wpEditToken"] = value; }
			else if (name == "wpWatchthis") { watch = value; }
		}
		if (typeof opts.watch == "undefined") {
			data["wpWatchthis"] = watch;
		} else if (opts.watch == true) {
			data["wpWatchthis"] = true;
		}
		var url = wk._URLBase + page.replace(/ /g, "_") + "&action=submit";
		client.open("POST", url, true);
		client.setRequestHeader("Content-Type", "application/x-www-form-urlencoded");
		client.onreadystatechange = function() {
			if (client.readyState != 4 || client.status != 200) {
				return;
			}
			if (opts.callback) {
				opts.callback(page, pagetext, data);
			}
		}
		client.send(wk._encodeData(data));
	},

	// Shows a basic edit window
	showEditWindow: function (xml, parent, text, summary) {
		if (parent == null) {
			parent = wk.makeElement('div');
		}
		if (typeof xml.getElementById == "function") {
			var form  = xml.getElementById('editform');
			form      = document.importNode(form, true);
			wk.spanText(parent, "").appendChild(form);
		} else {
			wk.spanText(parent, "");
			var i = xml.indexOf("<form");
			var j = xml.indexOf("</form>");
			xml   = xml.substring(i, j+7);
			wk.appendSpanText(parent, xml);
		}
		var ta    = document.getElementById('wpTextbox1');
		var sum   = document.getElementById('wpSummary');
		var time  = document.getElementById('wpStarttime');
		if (summary != null) {
			sum.value = summary;
		}
		if (typeof text == "string") {
			ta.value = text;
		} else if (typeof text == "function") {
			ta.value = text(ta.value);
		}
		return {form: form, text: ta, summary: sum, time: time, parent: parent};
	},

	// shows a small edit window with no descriptions or fancy tools.
	showEditWindowSmall: function (xml, parent, text, summary) {
		var editform = wk.showEditWindow(xml, parent, text, summary);
		editform.text.rows = 10;
		wk.spanText('editpage-copywarn', '');
		wk.spanText('editpage-specialchars', '');
		wk.spanText('specialchars', '');
		return editform;
	},

	// opens an edit page in a separate tab or window, with the given text and summary pre-loaded.
	showEditWindowExternal: function(page, text, summary) {
		var loadwin = function(win, newtext, newsummary) {
			var eb = win.document.getElementById('wpTextbox1');
			if (typeof newtext == "string") {
				eb.value = newtext;
			} else if (typeof newtext == "function") {
				eb.value = newtext(eb.value);
			}
			if (newsummary != null) {
				win.document.getElementById('wpSummary').value = newsummary;
			}
		};
		var w = window.open(wk._editURL(page));
		if (w.attachEvent) {
			w.attachEvent("onload", function() { loadwin(w, text, summary); });
		} else if (window.addEventListener) {
			w.addEventListener("load", function() { loadwin(w, text, summary) }, false);
		} else {
			w.document.addEventListener("load", function() { loadwin(w, out) }, false);
		}
	},

	makeElement: function(type, attr, children) {
		var elem = document.createElement(type);
		if (attr) {
			for (var a in attr) {
				elem.setAttribute(a, attr[a]);
			}
		}
		if (children == null) {
			return elem;
		} else if (children instanceof Array) {
			for (var i = 0; i < children.length; i++) {
				var child = children[i];
				if (typeof child == "string") {
					child = document.createTextNode(child);
				}
				elem.appendChild(child);
			}
		} else if (typeof children == "string") {
			elem.appendChild(document.createTextNode(children));
		} else {
			elem.appendChild(children);
		}
		return elem;
	},

	elementBuilder: function(type) {
		return function(attr, children) { return wk.makeElement(type, attr, children); }
	},

	makeButton: function (name, value, onclick) {
		var elem = wk.makeElement("input", {type:"button", name:name, value:value}, null);
		elem.onclick = onclick;
		return elem;
	},

	makeInput: function (name, value){
		return wk.makeElement("input", {type:"text", name:name, value:value}, null);
	},

	spanText: function(spanid, text) {
		var res = wk._getNode(spanid);
		if (res) res.innerHTML = ((text == null)?(""):(text));
		return res; 
	},

	appendSpanText: function (spanid, text) {
		var res = wk._getNode(spanid);
		if (res) res.innerHTML = res.innerHTML + text;
		return res;
	},

	appendChildren: function(parent, children) {
		var res = wk._getNode(parent);
		if (res == null) return;
		if (children instanceof Array) {
			for (var i = 0; i < children.length; i++) {
				if (typeof children[i] == 'string') {
					children[i] = document.createTextNode(children[i]);
				}
				res.appendChild(children[i]);
			}
		} else if (typeof children == 'string') {
			children[i] = document.createTextNode(children);
		}
		res.appendChild(children);
	},

	spanEditor: function(spanid) {
		return function(text) { return wk.spanText(spanid, text); }
	},

	wikiLink: function(pagename, link, action) {
		if (pagename == null) {
			pagename = "Main Page";
		}
		if (link == null) {
			link = pagename;
		}
		var a = wk.makeElement("a");
		if (action == null) {
			a.setAttribute("href", "/wiki/" + pagename.replace(/ /g, "_"));
		} else {
			a.setAttribute("href", "/w/index.php?title=" + pagename.replace(/ /g, "_") + "&" + action);
		}
		a.innerHTML = link;
		return a;
	},

	wikiLinkText: function (pagename, link, action) {
		if (link == null) {
			link = pagename;
		}
		if (action == null) {
			return "<a href=\"/wiki/" + pagename.replace(/ /g, "_") + "\">" + link + "</a>";
		} else {
			return "<a href=\"/w/index.php?title=" + pagename.replace(/ /g, "_") + "&" + action +
			"\">" + link + "</a>";
		}
	},

	toggleDisplay: function (elem, disp) {
		var res = wk._getNode(elem);
		if (res == null) return;
		if (disp != null) res.style.display = disp;
		else res.style.display = ((res.style.display == "none")?("block"):("none"));
	},

	toggleDisable: function (elem, disa) {
		var res = wk._getNode(elem);
		if (res == null) return;
		if (disa != null) res.disabled = ((disa == true)?(true):(false));
		else res.disabled = !res.disabled;
	},

	getElementsByClass: function (c, type) {
		var elems = new Array();
		if (type == null) type = "*";
		var els = document.getElementsByTagName(type);
		var pattern = new RegExp("(^|\\s)" + c + "(\\s|$)");
		for (i = 0, j = 0; i < els.length; i++) {
			if (pattern.test(els[i].className)) elems[j++] = els[i];
		}
		return elems;
	},

	testUserPermissions: function(level) {
		for (var i = 0; i < wgUserGroups.length; i++) {
			if (wgUserGroups[i] == level) {
				return true;
			}
		}
		if (level == "administrator") {
			return wk.testUserPermissions("bureaucrat");
		}
		return false;
	}
};

// </source>

//Print Version Creator
//Maintained by [[User:Whiteknight]]
/* 
  This script operates a semi-automagical print version creator gadget. This gadget is 
  located at [[User:Whiteknight/Print Version Gadget]]. It uses Whiteknight's Javascript
  core library to help with some of the common tasks and the AJAX-related tasks.
  Documentation and the gadget itself is located at 
  [[User:Whiteknight/Print Version Gadget]]. 
*/

var pv = {
  require:   2.64,
  formspan: "WKPVFormSpan",
  textspan: "WKPVSpan",
  statspan: "WKPVStatSpan",
  boxspan:  "WKPVFormSpanSub",
  form: null,
  text: "",       //raw converted text
  htmltext: ""   //html-formatted converted text
}

pv.getForm = function() { return pv._form; }

pv.createForm = function() {
  var form = wk.spanText(pv.formspan, "");
  if(form == null) return;
  pv._editB = wk.makeButton("WKPVEditB",    "Edit",    pv.edit);
  pv._displayB = wk.makeButton("WKPVDisplayB", "Display", pv.display);
  pv._booknameB = wk.makeInput("WKPVBookNameB", "");
  pv._listB = wk.makeElement("Textarea", {name:"WKPVListB", rows:"15", cols:"70"});
  pv._spanB = wk.makeElement("span", {id:pv.boxspan}, pv._listB);
  pv._form = wk.makeElement("form", {name:"WKPVFormB", method:"GET"}, [
      pv._booknameB,
      wk.makeButton("WKPVLoadB",    "Load",    pv.loadPage),
      " ",
      wk.makeButton("WKPVShowB",    "Show",    function(){wk.toggleDisplay(pv.boxspan, "block")}),
      wk.makeButton("WKPVHideB",    "Hide",    function(){wk.toggleDisplay(pv.boxspan, "none")}),
      " ",
      wk.makeButton("WKPVClearB",   "Clear",   pv.clear),
      " ",
      wk.makeButton("WKPVConvertB", "Convert", pv.convert),
      " ",
      pv._editB,
      pv._displayB,
      pv._spanB,
      wk.makeElement("br")
  ]);
  wk.toggleDisplay(pv._spanB, "none");
  wk.toggleDisable(pv._editB, true);
  wk.toggleDisable(pv._displayB, true);
  form.appendChild(pv._form);
}
if(typeof wk == 'object' && wk.testVersion(pv.require)) {
  addOnloadHook(pv.createForm);
}

pv.clear = function() {
  wk.spanText(pv.textspan, "");
  wk.spanText(pv.statspan, "");
  wk.toggleDisplay(pv.boxspan, "none");
  pv._listB.value = "";
  wk.toggleDisable(pv._editB, true);
  wk.toggleDisable(pv._displayB, true);
}

pv.bookName = "";
pv.getBookName = function() {
  pv.bookName = pv._booknameB.value;
  return pv.bookName;
}

pv.loadPage = function() {
  pv.getBookName();
  if(pv.bookName == "") {
      wk.spanText(pv.statspan, "Must enter a valid page name to load from");
      return;
  }
  wk.loadWikiText(pv.bookName, function(text){pv._listB.value = text;});
  wk.spanText(pv.statspan, "Successfully retrieved text from [[" +
    wk.wikiLinkText(pv.bookName) + 
    "]]. Click <b>Show</b> to see and edit the page text. Click <b>Convert</b> " + 
    "to create the print version."
  );
}

pv.setChapter = function (page, offset, xml) {
  page = page.replace("[[", "");
  page = page.replace("]]", "");
  var levels = page.split("/");
  var subpage = levels[levels.length - 1];
  if(subpage == "")
  {
    subpage = levels[levels.length - 2];
  }
  return ("{"+"{Print chapter heading|" + subpage + "}}\n{{:" + page + "}}\n"); 
}

pv.setSections = function (head, offset, xml) {
  head = head.replace(/=/g, "");
  head = head.replace(/^\s*/g, "");
  head = head.replace(/\s*$/g, "");
  return("{"+"{Print unit page|" + head + "}}\n\n");
}

pv.xmlFormat = function(xml) {
  xml = xml.replace(/''+/g, ""); //kill text formatting
  xml = xml.replace(/\{\{[^\}]+\}\}/g, ""); //kill templates
  xml = xml.replace(/\[\[category:[^\]]+\]\]/ig, ""); //kill categories
  xml = xml.replace(/\|[^\]\}]+\]\]/g, "]]"); //kill piped links
  xml = xml.replace(/\[\[\/([^\]]+)\/\]\]/g, "[[" + pv.bookName + "/$1]]"); //properly address relative links
  xml = xml.replace(/\[\[([^\]]+)\]\]/g, pv.setChapter); //prepare chapter entries    
  xml = xml.replace(/===([^=]+)===/g, pv.setSections);
  xml = xml.replace(/\* ?/g, ""); //kill bullets
  xml = xml.replace(/# ?/g, ""); //kill ordered lists
  xml = xml.replace(/==[^=]+==/g, ""); //kill headings
  xml = xml.replace(/\n\n\n+/g, "\n\n"); //kill extra whitespace

  xml = xml.replace(/</g, "&lt;");
  xml = xml.replace(/>/g, "&gt;");
  var header = "{{Print version notice}}\n" +
               "__NOTOC__ __NOEDITSECTION__\n" +
               "&lt;br style=\"page-break-after: always\"&gt;\n";
  if(pv.bookName != "") {
    header = header + "{{:" + pv.bookName + "/Cover}}\n" +
                      "&lt;br style=\"page-break-after: always\"&gt;\n";
  }
  xml = header + xml + "\n= GNU Free Documentation License =\n\n{{:GFDL|=}}\n\n";
  return xml;
}

pv.convert = function() {
  wk.toggleDisable(pv._editB, false);
  wk.toggleDisable(pv._displayB, false);
  wk.toggleDisplay(pv.boxspan, "none");
  pv.htmltext = pv.xmlFormat(pv._listB.value);
  pv.text = pv.htmltext.replace(/&gt;/g, ">");
  pv.text = pv.text.replace(/&lt;/g, "<");
  wk.spanText(pv.statspan, "Successfully converted text. " +
    "Click <b>Display</b> to show the current text for copy+paste. " +
    "Click <b>Edit</b> to edit and save the current text.");
  wk.toggleDisable(pv._editB, false);
  wk.toggleDisable(pv._displayB, false);
  wk.spanText(pv.textspan, "");
}

pv.display = function() {
  wk.toggleDisplay(pv.boxspan, "none");
  wk.spanText(pv.statspan, "<b>Print version text:</b><br>");
  if(pv.getBookName() != "") {
    wk.appendSpanText(pv.statspan, "Copy the text below into " +
      "[[<a href=\"/w/index.php?title=" + pv.bookName.replace(/ /g, "_") + 
      "/Print_Version&action=edit\">" + pv.bookName + "/Print Version</a>]] " +
      "<br><small><font color=\"#FF0000\" align=\"center\">Warning</font>: " + 
      "this text may need to be edited manually to remove unwanted " + 
      "templates, categories, links or odd formatting that the tool " +
      "cannot account for.</small><br>");
  }
  wk.spanText(pv.textspan, "<pre style=\"overflow: auto;\">" + pv.htmltext + "</pre>");
}

pv.edit = function() { 
  if(pv.getBookName() == "") {
    wk.spanText(pv.statspan, "Must enter a valid page name to edit");
    return;
  }  
  wk.spanText(pv.statspan, "Loading...");
  wk.toggleDisplay(pv.boxspan, "none");
  wk.loadEditPage(pv.bookName + "/Print Version", pv._edit2);
}

pv._edit2 = function(xml) {
  var edit = wk.showEditWindow(xml, pv.textspan);
  edit.summary.value = pv.bookName + 
    " Printable Version: Created by Whiteknight's Print Version Creator";
  edit.text.value = pv.text;
  wk.spanText(pv.statspan, 
    "Editing text for [[" + 
    wk.wikiLinkText(pv.bookName + "/Print Version") + 
    "]]. Edit the text below to remove any errors or artifacts " +
    "of the conversion process. Once you have made your edits, " +
    "click <b>Save Page</b>.");
}

