#define TREE_IMAGPART(NODE) (COMPLEX_CST_CHECK (NODE)->imag)

class tree_complex : public tree_common GTY(())
{
 public:
  TREECONSTRUCTOR(tree_complex)
  //  struct tree_common common;
  TreeRef real;
