#define  TREECONSTRUCTOR2(C) \
  C (); \
  C (void *); \
  C (C *); \
  C (C &); \
  C (const C &);  \
  operator bool () const;\
  C & operator =  (void *); \
  C & operator =  (const C &); \
  bool operator != (const C & ) const ; \
  bool operator != (C & ) const;\
  bool operator != (C & ) ;\
  bool operator != (C * ) ; \
  bool operator != (const int ) const ; /*!= 0*/		\
  bool operator != (const int ); /*!= 0*/		\
  bool operator == (const C & ) const ;		\
  bool operator == (C & ) ;\
  bool operator == (C * ) ;

#define  TREECONSTRUCTOR(C) \
  TREECONSTRUCTOR2(C)\
  C (tree_common *);

template <class T> class TRef
{
 public:
  TREECONSTRUCTOR(TRef)
  bool operator != (T *);
  bool operator != (const int & ) const;
  bool operator != ( const T & ) ;
  bool operator == ( T  ) ;
  T & operator =  (T *);
  T & operator =  (T &);

  operator T ();
};

template <class T> class TPtr
{
 public:
  TREECONSTRUCTOR(TPtr)
  bool operator != (T *);
  T & operator =  (T *);
  T & operator =  (T &);

  T * operator -> ();

  operator T ();
};

template <class T> class TreePtr
{
 public:
  TREECONSTRUCTOR(TreePtr)
  bool operator != (T *);
  T & operator =  (T *);
  T & operator =  (T &);

  operator tree_node* ();
  operator tree_decl * ();
  operator  tree_int_cst * ();
  operator  tree_real_cst* ();
  operator  tree_string* ();
  operator  tree_complex* ();
  operator  tree_vector* ();
  operator  tree_identifier* ();
  operator  tree_list* ();
  operator  tree_vec* ();
  operator  tree_exp* ();
  operator  tree_block* ();
  operator  tree_type* ();


  T * operator -> ();

  operator T ();

  bool operator != (const int &);

};


class TRefmachine_mode : public TRef<machine_mode>
