 public:
  TREECONSTRUCTOR(TRefmachine_mode)

    operator int();
  TRefmachine_mode & operator =  (TRefmachine_mode &);
  TRefmachine_mode & operator =  (machine_mode );
  TRefmachine_mode & operator ==  (machine_mode );

  //  TRefmachine_mode & operator ==  (int );

};


