{
 public:
  TREECONSTRUCTOR(TRefrtx_code)

    operator int();
  TRefrtx_code & operator =  (TRefrtx_code &);
  //  TRefrtx_code & operator =  (rtx_code );
  //  TRefrtx_code & operator ==  (rtx_code );
  //  TRefrtx_code & operator ==  (int );
