var cg = {
  version:   2.06,
  require:   2.64,
  textspan: "WKCGSpan",
  editspan: "WKCGFormSpanEdit",
  statspan: "WKCGStatSpan",
  formspan: "WKCGFormSpan"
}
cg.tags = "";
cg.notes = "";
cg.subjects = new Array(
