// <source lang="javascript">
// Log and <li> filter script, version [0.1.1]
// Created by [[w:User:Splarka|Splarka]] & amended [[User:Mike.lifeguard]]

var rightsLogOnly = (QueryString('type') == 'rights' || document.location.href.indexOf('Log/rights') != -1 );

if (	wgAction == 'history' ||
	wgCanonicalSpecialPageName == 'Search' ||
	wgCanonicalSpecialPageName == 'Log' ||
	wgCanonicalSpecialPageName == 'CheckUser' ||
	wgCanonicalSpecialPageName == 'LinkSearch' ||
	wgCanonicalSpecialPageName == 'Contributions' ||
	wgCanonicalSpecialPageName == 'IPBlockList' ||
	QueryString('lifilter'))
{
		$j(document).ready( function() {
			if (document.getElementById('p-cactions')) {
				addPortletLink('p-cactions', 'javascript:filterLiForm()' , 'Filter', 'ca-rxfilter', 'Open interactive log filter');
				if (window.AutoLoadLogsFilter || QueryString('lifilter')) {
					filterLiForm();
				}
			} else {
				filterLiForm(); // not a portlet skin, so add the filter anyway.
			}
		});
}

function filterLiForm() {
	var obj = document.getElementById('contentSub') || document.getElementById('topbar');
	if ( !obj ) { return; }
	var instructions = (rightsLogOnly) ? 'Filter by space-delimited list of rights.' : 'Filter the plain text representation of this list.';
	// generate the form
	$j('#ca-rxfilter').css('display', 'none');
	var form = '<div id="rfform" style="color:black;"></div>';
	var field = '<fieldset>';

	field += '<p>'+instructions+'</p>';
	field += '<legend>Filter list</legend>';

	if (rightsLogOnly) {
		field += '<select id="rfselect"><option>+ rights</option><option>- rights</option>';
		field += '<option>either</option><option>all</option></select> ';
	}

	field += '<label for="rfinput1">Match:</label> <input id="rfinput1" type="text" /> ';
	field += '<input id="rfinput2" type="checkbox" /> <label for="rfinput2">Invert</label> ';
	field += '</fieldset>';
	$j(obj).append(
		$j(form).append(
			$j(field).append( $j('<input type="button" value="filter" />').click( function() { filterLi(false); } ) )
				.append( $j('<input type="button" value="highlight" />').click( function() { filterLi(true); } ) )
		)
	);
	return;
}

function filterLi(hilight) {
	// grab options from form
	if (rightsLogOnly) var type = document.getElementById('rfselect').selectedIndex;
	var invert = document.getElementById('rfinput2').checked;
	var srch = document.getElementById('rfinput1').value;
	var rx = new RegExp(srch,'ig');
	var rightrx = /^.*?User\:.*? from (.*?) to (.*?) \u200E.*?$/i;   // the regex used to grab the Special:Log/rights changes

	// grab the list of all <li> in the content
	var bod = document.getElementById('bodyContent') || document.getElementsByTagName('body')[0];
	$j('li', bod).each( function() {
		var look = '';
		if (rightsLogOnly) {
			// Special:Log/rights
			var text = $j(this).text();
			var oldr = text.replace(rightrx,'$1').replace(/\, /g,' ');
			var newr = text.replace(rightrx,'$2').replace(/\, /g,' ');
			var oldrl = oldr.split(' '); var newrl = newr.split(' ');
			for (var j=0; j<oldrl.length; ++j) {
				for (var k=0; k<newrl.length; ++k) {
					if (oldrl[j]==newrl[k]) {
						oldrl[j] = ''; newrl[k] = '';
 					}
				}
			}
			var remr = oldrl.join(' '); var addr = newrl.join(' ');
			switch (type) {
				case 0: look = addr; break;
				case 1: look = remr; break;
				case 2: look = remr + ' ' + addr; break;
				case 3: look = oldr + ' ' + newr; break;
			}
		} else {
			look = $j(this).text();
		}
		$j(this).css( { display: '', 'backgroundColor': '' } );

		if ( (look.search(rx) != -1 && !invert) || (look.search(rx) == -1 && invert) ) {
			if (hilight) $j(this).css('backgroundColor', '#ffff99');
		} else {
			if (!hilight) $j(this).css('display', 'none');
		}
	});
}

function QueryString(p) {
	var re = RegExp('[&?]' + p + '=([^&]*)');
	var matches;
	if (matches = re.exec(document.location)) {
		try { 
			return decodeURI(matches[1]);
		} catch (e) {}
	}
	return null;
}

// </source>

== Motivation ==
You want to create an easy-to-use specialized chart using a generic charting service such as Google Charts

== Method ==

The [http://code.google.com/apis/chart/ GoogleChart API] creates PNG-format charts from data passed in the URL line.  

One use of the service would be to generate a Tufte [http://www.edwardtufte.com/bboard/q-and-a-fetch-msg?msg_id=0001OR sparkline]. This script uses random data to generate a small sparkline-like graphic. With a bit more work, the additional features such as  minimum, maximum and normal bands should be able to be added. A line chart (cht=lc) includes axes but these can be removed by using an undocumented feature in which the chart type is specified as lfi [http://24ways.org/2007/tracking-christmas-cheer-with-google-charts ]

The script uses function overloading in XQuery which allows two functions to have the same name but different numbers of parameters. The more general function has parameters for the sequence of values and the min and max to be used in scaling the values.  The second function (with the same name) accepts only the values and calculates the min and max from the data before calling the more general function to complete the task.


<source lang="xml">
(:  This script illustrates the use of the GoogleChart API to generate a sparkline-like graphic

:)
declare option exist:serialize "method=html media-type=text/html";

declare function local:simple-encode(
         $vals as xs:decimal* ,
         $min as xs:decimal,
         $max as xs:decimal) 
     as xs:string {
(: encode the sequence of numbers as a string according to the simple encoding scheme.
    the data values are encoded in the characters A-Z,a-z,0-9 giving a range from 0 to 61
:)
  let $scale := 62.0 div ($max - $min) 
  let $simpleEncode := "ABCDEFGHIJKLMNOPQRSTUVWXYZabcdefghijklmnopqrstuvwxyz0123456789"
  return  
      string-join( for $x in $vals
      let $n := floor( ($x - $min) * $scale)
      return substring($simpleEncode,$n+1,1)
      ,"")
};

declare function local:simple-encode($vals as xs:decimal*) as xs:string {
(: compute the minimulm and maximum values, then call the more general  function to encode :)
  let $min := min($vals)
  let $max := max($vals)
  return local:simple-encode($vals,$min,$max)
};

declare function local:sparkline(
            $data as xs:decimal* ,
            $fontHeight as xs:integer, 
            $pointSize as xs:integer, 
            $label as xs:string )
        as element(span) {
(: create a span element containing the line chart of the data, the name of the data set 
   and the last data value
   fontHeight and pointSize are  defined in pixels 
:)
let $codeString := local:simple-encode($data)
let $width := count($data) * $pointSize
let $last := $data[last()]
let $title :=concat( "Graph of  ",$label, " data: ",count($data)," values, min ",min($data), " max ", max($data))
return
   <span>
          <img src="http://chart.apis.google.com/chart?chs={$width}x{$fontHeight}&amp;chd=s:{$codeString}&amp;cht=lfi"
alt="{$title}"  title="{$title}"
                    />
          <font style="font-size:{$fontHeight}px"> {$label}&#160;{$last}</font>
    </span>

};

(: generate some random data :)
let $data := for $i in (1 to 100) return floor(math:random() * 10)
return local:sparkline($data,15,1,"Random")
</source>

[http://www.cems.uwe.ac.uk/xmlwiki/Graph/randomSparkline.xq Random Sparkline]


