/* Change [[MediaWiki:Edittools]] into true buttons. */

$(document).ready( function($) {
	var $sb = $('#specialchars').find('.specialbasic'), bl = $sb.length, $sel;

	if ( bl > 1 ) {
		$sel = $('<select>').data( 'at', 0 ).change( function(e) {
			var $this = $(this), i = $this.val(), last = $this.data( 'at' );
			if ( last !== i ) {
				$sb.eq( last ).css( 'display', 'none' );
				$sb.eq( i ).css( 'display', 'inline' );
				$this.data( 'at', i );
			}
		});
	}

	$sb.each( function(i) {
		var id = $(this)
			.css( 'display', i > 0 ? 'none' : 'inline' )
			.find( 'a' ).replaceWith( function() {
				var $this = $(this), onclick = $this.attr( 'onclick' );
				return $( '<button>', { type: 'button', text: $this.text() } )
					.blur()
					.click( $.isFunction( onclick ) ? onclick : Function( onclick ) );
			})
			.end()
			.attr( 'id' )
			.replace( /\.([0-9A-F]{2})/g, '%$1' )
			.replace( /_/g, ' ' );

		if ( bl > 1 ) {
			$sel.append( '<option value=' + i + '>' + decodeURIComponent( id ) + '</option>' );
		}
	}).end().prepend( $sel );
});

