// After adding to your monobook.js, navigate to [[Special:Massblock]] to use the tool.

function getParameter(p) {
  var re = new RegExp('&'+p+'=([^&]*)','i');
  var c = window.location.search; 
  return unescape((c=c.replace(/^\?/,'&').match(re)) ?c=c[1] :c="");
}

function massblock() {
  if (wgPageName == "Special:Massblock") {
    document.title = "Mass block tool";
    document.getElementById("content").innerHTML = "When using this tool, you should know what great damage it can do. List of IPs to block, one on each line please:<br><textarea id=\"iplist\" columns=\"24\" rows=\"10\"></textarea><br /><br />Expiry: <input type=\"text\" value=\"1 year\" id=\"expiry\"><br />Reason: <input type=\"text\" value=\"[[w:open proxy|Open proxy]] or [[w:zombie computer|zombie]] ([[m:WM:OP|more info]])\" id=\"reason\"><br /><input type=\"checkbox\" id=\"ao\" /> Anon only<br /><input type=\"checkbox\" id=\"acb\" checked=\"checked\" /> Prevent account creation<br /><br />Blocks/min: <input type=\"text\" value=\"10\" id=\"epm\"><br /><button onclick=\"massblock2()\">Block IPs</button> <button onclick=\"massblock4()\">Abort</button><div style=\"z-index:-1;position:relative;top:0px;left:0px\"><iframe name=\"blockframe0\" width=\"1px\" height=\"1px\"></iframe><iframe name=\"blockframe1\" width=\"1px\" height=\"1px\"></iframe><iframe name=\"blockframe2\" width=\"1px\" height=\"1px\"></iframe><iframe name=\"blockframe3\" width=\"1px\" height=\"1px\"></iframe><iframe name=\"blockframe4\" width=\"1px\" height=\"1px\"></iframe><iframe name=\"blockframe5\" width=\"1px\" height=\"1px\"></iframe></div>";
  } else if (document.location.search.match("__MASSBLOCK__")) {
      document.getElementById("wpAnonOnly").checked = getParameter("ao") == "1";
      document.getElementById("wpCreateAccount").checked = getParameter("acb") == "1";
      document.getElementById("mw-bi-other").value = getParameter("expiry").replace("+"," ","g");
      document.getElementById("mw-bi-reason").value = getParameter("reason").replace("+"," ","g");
      setTimeout("document.getElementById(\"blockip\").submit()", 500);
  }
}

var wgBlocksToDo;
var wgBlocksToDoIndex;
var wgBlocksToDoInterval = 0;
var wgBlockFrame;
function massblock2() {
  if (!parseFloat(document.getElementById("epm").value)) return;
  wgBlocksToDo = new Array();

  iplist = document.getElementById("iplist").value.split("\n");
  for (i=0;i<iplist.length;i++) {
    wgBlocksToDo[i] = iplist[i].split(":")[0];
  }

  mbcode = "massblock3(wgBlocksToDo[wgBlocksToDoIndex++]);if (wgBlocksToDoIndex >= wgBlocksToDo.length) massblock4();";

  wgBlocksToDoIndex = wgBlockFrame = 0;
  eval(mbcode);
  wgBlocksToDoInterval = setInterval(mbcode, (60 / parseFloat(document.getElementById("epm").value)) * 1000);
}

function massblock3(ipToBlock) {
  if (ipToBlock + "" == "undefined") return;

  iplistobj = document.getElementById("iplist");
  if (iplistobj.value.indexOf("\n") == -1) iplistobj.value = "";
  iplistobj.value = iplistobj.value.substring(iplistobj.value.indexOf("\n") + 1);

  if (ipToBlock == "") return;

  frames["blockframe" + wgBlockFrame++].location.href = wgServer + wgScript + "?title=Special:Blockip&ip=" + ipToBlock + "&__MASSBLOCK__=1&expiry=" + document.getElementById("expiry").value + "&reason=" + document.getElementById("reason").value + "&ao=" + (document.getElementById("ao").checked ? "1" : "0") + "&acb=" + (document.getElementById("acb").checked ? "1" : "0");
  if (wgBlockFrame == 6) wgBlockFrame = 0;
}

function massblock4() {
  clearInterval(wgBlocksToDoInterval);
}

addOnloadHook(massblock);

function QuickBlock (blockOther,blockReason,anonOnly,createAccount,enableAutoblock,emailBan,hideName,watchUser,allowUsertalk) {
	blockForm = document.getElementById('blockip');
	inputs = blockForm.getElementsByTagName('input');
	for (i=0; i<inputs.length; i++) {
		if (inputs[i].name == 'wpBlockOther') {
			inputs[i].value = blockOther;
			continue;
		}
		if (inputs[i].name == 'wpBlockReason') {
			inputs[i].value = blockReason;
			continue;
		}
		if (inputs[i].name == 'wpAnonOnly') {
			if (anonOnly==1) {
				inputs[i].checked = true;
			}
			else {
				inputs[i].checked = false;
			}
			continue;
		}
		if (inputs[i].name == 'wpCreateAccount') {
			if (createAccount==1) {
				inputs[i].checked = true;
			}
			else {
				inputs[i].checked = false;
			}
			continue;
		}
		if (inputs[i].name == 'wpEnableAutoblock') {
			if (enableAutoblock==1) {
				inputs[i].checked = true;
			}
			else {
				inputs[i].checked = false;
			}
			continue;
		}
		if (inputs[i].name == 'wpEmailBan') {
			if (emailBan==1) {
				inputs[i].checked = true;
			}
			else {
				inputs[i].checked = false;
			}
			continue;
		}
		if (inputs[i].name == 'wpHideName') {
			if (hideName==1) {
				inputs[i].checked = true;
			}
			else {
				inputs[i].checked = false;
			}
			continue;
		}
		if (inputs[i].name == 'wpWatchUser') {
			if (watchUser==1) {
				inputs[i].checked = true;
			}
			else {
				inputs[i].checked = false;
			}
			continue;
		}
		if (inputs[i].name == 'wpAllowUsertalk') {
			if (allowUsertalk==1) {
				inputs[i].checked = true;
			}
			else {
				inputs[i].checked = false;
			}
			continue;
		}
	}
	blockForm.submit();
}

function userIsInGroup (group) {
//are they in a given local group?
	if (wgUserGroups) {
		if (!group || group.length == 0) group = '*';
		if (wgUserGroups.join (' ').indexOf (group) != -1){
			return true;
		}
		else{
			return false;
		}
	}
	return false;
}
function userIsInGlobalGroup (group) {
//are they in a given global group?
	if (wgGlobalGroups) {
		if (!group || group.length == 0) group = '*';
		if (wgGlobalGroups.join (' ').indexOf (group) != -1){
			return true;
		}
		else{
			return false;
		}
	}
	return false;
}

function addblockoptions() {//blockOther,blockReason,anonOnly,createAccount,enableAutoblock,emailBan,hideName,watchUser,allowUsertalk
	addPortletLink('p-cactions',
		'javascript:('+QuickBlock+')("31 hours","recent vandalism",1,1,0,0,0,0,1)',
		'vandal: 31h',
		'p-block-31h');
	addPortletLink('p-cactions',
		'javascript:('+QuickBlock+')("1 week","repeat vandalism",1,1,1,0,0,0,1)',
		'vandal: 1w',
		'p-block-week');
	addPortletLink('p-cactions',
		'javascript:('+QuickBlock+')("1 month","chronic vandalism",1,1,1,0,0,0,1)',
		'vandal: 1m',
		'p-block-month');
	addPortletLink('p-cactions',
		'javascript:('+QuickBlock+')("infinite","vandalism-only account",0,1,0,0,0,0,1)',
		'vandal: indef',
		'p-block-vandalism');
	addPortletLink('p-cactions',
		'javascript:('+QuickBlock+')("3 months","cross-wiki issues",0,1,1,0,0,0,0)',
		'xwiki: 3mo',
		'p-block-xwiki');
	addPortletLink('p-cactions',
		'javascript:('+QuickBlock+')("1 year","[[w:Open proxy|Open proxy]] or [[w:Zombie computer|zombie]] ([[m:WM:NOP|info]])",1,1,0,0,0,0,1)',
		'op',
		'p-block-proxy');
	if(userIsInGroup('checkuser')||userIsInGlobalGroup('steward')) addPortletLink('p-cactions',
		'javascript:('+QuickBlock+')("infinite","checkuserblock",0,1,0,1,0,0,0)',
		'checkuserblock-user',
		'p-block-cu-user');
	if(userIsInGroup('checkuser')||userIsInGlobalGroup('steward')) addPortletLink('p-cactions',
		'javascript:('+QuickBlock+')("1 year","checkuserblock",0,1,0,0,0,0,1)',
		'checkuserblock-IP',
		'p-block-cu-ip');
	if(userIsInGroup('oversight')||userIsInGlobalGroup('steward')) addPortletLink('p-cactions',
		'javascript:('+QuickBlock+')("infinite","checkuserblock",0,1,1,1,1,0,0)',
		'invisible',
		'p-block-invisible');
	//blockOther,blockReason,anonOnly,createAccount,enableAutoblock,emailBan,hideName,watchUser,allowUsertalk
}
if ( wgCanonicalSpecialPageName == 'Blockip' )addOnloadHook(addblockoptions);

