function putOutList(list) {
  var articleName, bookName = list[0];
  var out = '== ' + bookName.replace(/_/g, " ") +' ==\n';
  out += ':[[' + bookName.replace(/_/g, " ") + ']]\n';
  for (var i = 1; i < list.length; i++) {
