  //      self.addSubpage(dragged);
  //    }
  //  } else {  //it's a heading
  //    dragged.parent.removeHeading(dragged);
  //    self.addHeading(dragged);
  //  } 
  //  vbd.visual();
  //}, false);
  //this.box.addEventListener("mouseover", function(event) {
  //  if(vbd.lastClicked == null || event.target != self.box || vbd.lastClicked == self) return;
