e_name = source_addr source_iface.node_device_type = 'PCAP' source_iface.save false end target_iface = IpInterface.find_or_initialize_by_address(target_addr) if target_iface.new_record? target_iface.node_name = target_addr target_iface.node_device_type = 'PCAP' target_iface.save false end if pkt.tcp? || pkt.udp? traffic = Traffic.first(:conditions => { :source_layer3_interface_id => source_iface.layer3_interface.id, :target_layer3_interface_id => target_iface.layer3_interface.id, :port => pkt.dport}) else traffic = Traffic.first(:conditions => { :source_layer3_interface_id => source_iface.layer3_interface.id, :target_layer3_interface_id =>target_iface.layer3_interface.id }) end unless traffic if pkt.tcp? || pkt.udp? traffic = Traffic.create :source_layer3_interface => source_iface.layer3_interface, :target_layer3_interface => target_iface.layer3_interface, :port => pkt.dport, :description => "PCAP" else traffic = Traffic.create :source_layer3_interface => source_iface.layer3_interface, :target_layer3_interface => target_iface.layer3_interface,:description => "PCAP" end puts "Added traffic -- #{source_addr} ==> #{target_addr}" end end end end end end end # if ['-h', '--help'].include?(ARGV[0]) print_help else file_dir = ARGV.pop if File.directory?(file_dir) Find.find(file_dir) do |path| if File.file?(path) parse(path, ARGV) end end else parse(file_dir, ARGV) end end
