    txt.value = autoEdUnicodeControlChars(txt.value);
    txt.value = autoEdTemplates(txt.value);
    txt.value = autoEdTablestoWikitext(txt.value);
    txt.value = autoEdExtraBreaks(txt.value);
    txt.value = autoEdLinks(txt.value);
}

